src/app/globals.css.content:
@import "tailwindcss";

/* === Apple-inspired Design System === */

:root {
  /* Layout */
  --max-width: 1200px;
  --content-padding: 1.5rem;
  --section-padding: 4rem 0;
  --transition: all 0.2s ease;

  /* Typography â€” San Francisco / system UI */
  --font-sans: -apple-system, BlinkMacSystemFont, "SF Pro Text", "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  --font-mono: SFMono-Regular, Menlo, Monaco, Consolas, "Liberation Mono", "Courier New", monospace;

  /* Font sizes */
  --text-xs: 0.75rem;   /* 12px */
  --text-sm: 0.875rem;  /* 14px */
  --text-base: 1rem;    /* 16px */
  --text-lg: 1.125rem;  /* 18px */
  --text-xl: 1.375rem;  /* 22px */
  --text-2xl: 1.75rem;  /* 28px */
  --text-3xl: 2rem;     /* 32px */
  --text-4xl: 2.5rem;   /* 40px */
  --text-5xl: 3rem;     /* 48px */

  /* Font weights */
  --font-thin: 200;
  --font-light: 300;
  --font-normal: 400;
  --font-medium: 500;
  --font-semibold: 600;
  --font-bold: 700;

  /* Colors â€” neutral monochrome with subtle accents */
  --white: #ffffff;
  --background: var(--white);
  --background-secondary: #f5f5f5;
  --foreground: #000000;
  --foreground-secondary: #555555;
  --foreground-tertiary: #888888;

  --primary: #000000;
  --primary-hover: #333333;
  --primary-inverse: #ffffff;

  --secondary: #f5f5f5;
  --secondary-hover: #e5e5e5;

  --accent: #048542; /* iOS blue accent */
  --accent-hover: #14b547;
  --accent-foreground: #ffffff;

  --border: var(--accent);
  --border-light: color-mix(in srgb, var(--accent) 40%, transparent);
  --input-bg: #f9f9f9;
  --ring: #007aff;

  --success: #34c759;
  --warning: #ffcc00;
  --error: #ff3b30;

  /* Subtle shadows */
  --shadow-xs: 0 1px 2px rgba(0, 0, 0, 0.05);
  --shadow-sm: 0 1px 4px rgba(0, 0, 0, 0.06);
  --shadow-md: 0 2px 8px rgba(0, 0, 0, 0.08);
  --shadow-lg: 0 4px 12px rgba(0, 0, 0, 0.12);

  /* Rounded corners */
  --radius-xs: 0.25rem;
  --radius-sm: 0.5rem;
  --radius: 0.75rem;
  --radius-md: 1rem;
  --radius-lg: 1.5rem;
  --radius-xl: 2rem;
  --radius-full: 9999px;
}

/* === Base styles === */

html {
  scroll-behavior: smooth;
}

body {
  margin: 0;
  padding: 0;
  background: var(--background);
  color: var(--foreground);
  font-family: var(--font-sans);
  font-size: var(--text-base);
  font-weight: var(--font-normal);
  line-height: 1.5;
  -webkit-font-smoothing: antialiased;
  text-rendering: optimizeLegibility;
}

/* Base link styles */
a {
  color: inherit;
  text-decoration: none;
  transition: 200ms ease-in-out;
}

button {
  font-family: var(--font-sans);
  border: none;
  background: none;
  cursor: pointer;
  transition: 200ms ease-in-out;
}

input,
textarea {
  border: 1px solid var(--border-light);
  border-radius: var(--radius);
  background: var(--input-bg);
  padding: 0.75rem 1rem;
  font-family: var(--font-sans);
  color: var(--foreground);
  transition: 200ms ease-in-out;
}

input:focus,
textarea:focus {
  border-color: var(--ring);
  outline: none;
  box-shadow: 0 0 0 2px rgba(0, 122, 255, 0.3);
}

/* Specific styles for the Get Started button */
button.inline-flex.items-center.px-10.py-4 {
  background-color: #01753f !important;
}

button.inline-flex.items-center.px-10.py-4:hover {
  background-color: #016030 !important;
}

/* === Clean Hover Animations === */

/* Nav links - simple underline on hover */
.nav-link {
  transition: text-decoration 200ms ease-in-out;
}

.nav-link:hover {
  text-decoration: underline;
  text-underline-offset: 4px;
  text-decoration-thickness: 1px;
}

/* Logo - opacity on hover */
.logo-link {
  transition: opacity 200ms ease-in-out;
}

.logo-link:hover {
  opacity: 0.8;
}

/* Footer links - simple underline on hover */
.footer-link {
  transition: text-decoration 200ms ease-in-out;
}

.footer-link:hover {
  text-decoration: underline;
  text-underline-offset: 4px;
  text-decoration-thickness: 1px;
}

/* Optional: Dark mode */

